    async createCategory(category: ICategory): Promise<ICategory | null> {
        try {
            const categoryName = category.name.trim().toLowerCase();

            // Ensure exact match using `^` (start) and `$` (end)
            const isCategoryExists = await Category.findOne({
                name: { $regex: `^${categoryName}$`, $options: "i" },
            });

            if (isCategoryExists) throw new BadRequestError("Category already exists");

            const newCategory = new Category(category);
            await newCategory.save();

            return newCategory;
        } catch (error) {
            throw error;
        }
    }

    async findCategories(id: string, page: string): Promise<ICategory[] | null> {
        try {
            const res = await Category.aggregate([
                {
                    $facet: {
                        count: [{ $match: { name: { $regex: id, $options: "i" } } }, { $count: "categoryCount" }],
                        categories: [
                            { $match: { name: { $regex: id, $options: "i" } } },
                            { $sort: { createdAt: -1 } },
                            { $skip: (parseInt(page) - 1) * 10 },
                            { $limit: 10 },
                        ],
                    },
                },
            ]);

            return res;
        } catch (err) {
            throw err;
        }
    }
    async updateCategory(category: ICategory): Promise<ICategory | null> {
        try {
            const updatedCategory = await Category.findByIdAndUpdate(category._id, category, { new: true });

            return updatedCategory;
        } catch (err) {
            throw err;
        }
    }
