    async createSize(size: string): Promise<Record<string, any> | null> {
        try {
            const isSizeExists = await Size.findOne({
                size: { $regex: `^${size}$`, $options: "i" },
            });
            if (isSizeExists) throw new BadRequestError("Duplicate Size");
            const newSize = new Size({ size: size });
            await newSize.save();
            return newSize;
        } catch (err) {
            throw err;
        }
    }
