    async getAllProductsForAdmin({ category, search, page, status }: any): Promise<IProduct[] | null> {
        try {
            let query: any = {};
            if (search) query.name = { $regex: search, $options: "i" }; // Case-insensitive search
            if (status === "in stock") {
                query.stock = true;
            } else if (status === "out of stock") {
                query.stock = false;
            }

            const pipeline: any[] = [
                {
                    $lookup: {
                        from: "categories", // MongoDB collection name
                        localField: "category", // Product field
                        foreignField: "_id", // Category field
                        as: "categoryDetails",
                    },
                },
                { $unwind: "$categoryDetails" },
                {
                    $lookup: {
                        from: "frames",
                        localField: "frame",
                        foreignField: "_id",
                        as: "frameDetails",
                    },
                },
                { $unwind: "$frameDetails" }, // Flatten category array
                { $match: query }, // Apply other filters
            ];

            // Category filter
            if (category) {
                pipeline.push({
                    $match: {
                        "categoryDetails.name": { $regex: category, $options: "i" },
                    },
                });
            }

            // Pagination
            pipeline.push({ $skip: (Number(page) - 1) * 10 });
            pipeline.push({ $limit: 10 });

            const products = await Product.aggregate(pipeline);

            return products;
        } catch (err) {
            throw err;
        }
    }

    async updateProductStatus(id: string): Promise<any> {
        try {
            return await Product.updateOne(
                { _id: id }, // Find the product by its ID
                [
                    {
                        $set: {
                            stock: {
                                $cond: {
                                    if: { $eq: ["$stock", true] },
                                    then: false,
                                    else: true,
                                },
                            },
                        },
                    },
                ]
            );
        } catch (err) {
            throw err;
        }
    }

