    async createBanner(bannerData: any): Promise<Record<string, any> | null> {
        try {
            let updatedBanner;

            const isBanner = await Banner.findOne({});

            if (isBanner) {
                await Banner.updateOne({}, { $addToSet: { banners: bannerData } });
                updatedBanner = await Banner.findOne({});
            } else {
                updatedBanner = await Banner.create({ banners: [bannerData], isSlide: true, time: 0 });
            }

            return updatedBanner;
        } catch (err) {
            throw err;
        }
    }

